tasks.command:
mkdir -p styles/scss/imports; mkdir -p styles/dist/css; mkdir -p js; mkdir -p assets; echo "@import url('https://fonts.googleapis.com/css2?family=Poppins:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap');

@import 'imports/mixins-keyframes';
@import 'imports/variables';
@import 'imports/resets';" > styles/scss/style.scss; echo "*,
*::before,
*::after {
  box-sizing: border-box;
}

body, h1, h2, h3, h4, h5, h6, p, blockquote, pre, dl, dd, ol, ul, figure, hr, fieldset, legend, button, input, textarea {
  margin: 0;
  padding: 0;
}

body {
  font-family: Arial, sans-serif;
  line-height: 1.6;
  color: #333;
}

ol, ul {
  list-style: none;
}

fieldset {
  border: none;
}

table {
  border-collapse: collapse;
  border-spacing: 0;
}

a {
  text-decoration: none;
  color: inherit;
}

button {
  background: none;
  border: none;
  cursor: pointer;
}

img {
  border: none;
  max-width: 100%;
  height: auto;
}" > styles/scss/imports/_resets.scss; echo "const stylesheets = ["https://site-assets.fontawesome.com/releases/v6.6.0/css/all.css", "https://site-assets.fontawesome.com/releases/v6.6.0/css/sharp-duotone-solid.css", "https://site-assets.fontawesome.com/releases/v6.6.0/css/sharp-thin.css", "https://site-assets.fontawesome.com/releases/v6.6.0/css/sharp-solid.css", "https://site-assets.fontawesome.com/releases/v6.6.0/css/sharp-regular.css", "https://site-assets.fontawesome.com/releases/v6.6.0/css/sharp-light.css", "./styles/dist/css/style.css"]; stylesheets.forEach(href => { const link = document.createElement('link'); link.rel = 'stylesheet'; link.href = href; document.head.appendChild(link); });" > js/styles.js; echo "<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <script src="./js/styles.js"></script>
  <title>Document</title>
</head>
<body>
  <header>

  </header>

  <main>

  </main>

  <footer>

  </footer>

  <script src="./js/general.js"></script>
</body>
</html>" > index.html
